import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  cityName: ''
};

const cityName = createSlice({
    name:'getcityname',
    initialState,
    reducers: {
    setCityName: (state, action) => {
      state.cityName = action.payload;
    }
}
});

export const { setCityName } = getCityName.actions;
export default getCityName.reducer;
