import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  cityName: ''
};

const cityNameStore = createSlice({
    name:'cityname',
    initialState,
    reducers: {
    setCityName: (state, action) => {
      state.cityName = action.payload;
    }
}
});

export const { setCityName } = cityNameStore.actions;
export default cityNameStore.reducer;
